import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link as RouterLink } from 'react-router-dom';
import { 
  Box, Container, Typography, Button, Paper, 
  Grid, TextField, FormControl, InputLabel, Select, MenuItem,
  CircularProgress, Alert, FormHelperText
} from '@mui/material';
import { getDepartmentById, createDepartment, updateDepartment } from '../../services/departmentService';

const DepartmentForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isEditMode = Boolean(id);
  
  const [loading, setLoading] = useState(isEditMode);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  
  const [formData, setFormData] = useState({
    name: '',
    head: '',
    description: '',
    foundedYear: 2000,
    location: '',
    status: 'ACTIVE',
    budget: '',
    email: '',
    phone: ''
  });

  useEffect(() => {
    if (isEditMode) {
      const fetchDepartment = async () => {
        try {
          const departmentData = await getDepartmentById(id);
          
          setFormData({
            name: departmentData.name || '',
            head: departmentData.head || '',
            description: departmentData.description || '',
            foundedYear: departmentData.foundedYear || 2000,
            location: departmentData.location || '',
            status: departmentData.status || 'ACTIVE',
            budget: departmentData.budget ? departmentData.budget.toString() : '',
            email: departmentData.email || '',
            phone: departmentData.phone || ''
          });
          
          setLoading(false);
        } catch (err) {
          console.error('Error loading department:', err);
          setError('Failed to load department data. Please try again.');
          setLoading(false);
        }
      };

      fetchDepartment();
    }
  }, [id, isEditMode]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Create a copy of the form data to avoid modifying state directly
      const submissionData = { ...formData };
      
      // Convert string values to appropriate types
      if (typeof submissionData.foundedYear === 'string') {
        submissionData.foundedYear = parseInt(submissionData.foundedYear, 10);
      }
      
      if (submissionData.budget) {
        submissionData.budget = parseInt(submissionData.budget, 10);
      }
      
      console.log('Submitting department data:', submissionData);
      
      if (isEditMode) {
        await updateDepartment(id, submissionData);
      } else {
        await createDepartment(submissionData);
      }
      
      setSuccess(true);
      setLoading(false);
      
      // Redirect after a short delay
      setTimeout(() => {
        navigate('/departments');
      }, 1500);
    } catch (err) {
      console.error('Error saving department:', err);
      setError('Failed to save department. Please check your input and try again.');
      setLoading(false);
    }
  };

  return (
    <Box className="page-container">
      <Container maxWidth="md">
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <Button
            variant="outlined"
            component={RouterLink}
            to="/departments"
            sx={{ mr: 2 }}
          >
            Back
          </Button>
          <Typography variant="h4" component="h1">
            {isEditMode ? 'Edit Department' : 'Add New Department'}
          </Typography>
        </Box>
        
        <Paper elevation={3} sx={{ p: 4 }}>
          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}
          
          {success && (
            <Alert severity="success" sx={{ mb: 3 }}>
              Department {isEditMode ? 'updated' : 'created'} successfully!
            </Alert>
          )}
          
          {loading && !error ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
              <CircularProgress />
            </Box>
          ) : (
            <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="name"
                    label="Department Name"
                    value={formData.name}
                    onChange={handleChange}
                    fullWidth
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="head"
                    label="Department Head"
                    value={formData.head}
                    onChange={handleChange}
                    fullWidth
                    required
                    helperText="Name and title of department head"
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    name="description"
                    label="Description"
                    value={formData.description}
                    onChange={handleChange}
                    fullWidth
                    multiline
                    rows={3}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="foundedYear"
                    label="Founded Year"
                    type="number"
                    value={formData.foundedYear}
                    onChange={handleChange}
                    fullWidth
                    required
                    inputProps={{ min: 1800, max: new Date().getFullYear() }}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="location"
                    label="Location"
                    value={formData.location}
                    onChange={handleChange}
                    fullWidth
                    helperText="Building and floor"
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="email"
                    label="Department Email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    fullWidth
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="phone"
                    label="Department Phone"
                    value={formData.phone}
                    onChange={handleChange}
                    fullWidth
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="budget"
                    label="Annual Budget"
                    value={formData.budget}
                    onChange={handleChange}
                    fullWidth
                    type="number"
                    InputProps={{
                      startAdornment: '$',
                    }}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <FormControl fullWidth required>
                    <InputLabel>Status</InputLabel>
                    <Select
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                      label="Status"
                    >
                      <MenuItem value="ACTIVE">Active</MenuItem>
                      <MenuItem value="INACTIVE">Inactive</MenuItem>
                    </Select>
                    <FormHelperText>Current state of the department</FormHelperText>
                  </FormControl>
                </Grid>
                <Grid item xs={12}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                    <Button
                      variant="outlined"
                      onClick={() => navigate('/departments')}
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      disabled={loading}
                    >
                      {isEditMode ? 'Update Department' : 'Add Department'}
                    </Button>
                  </Box>
                </Grid>
              </Grid>
            </Box>
          )}
        </Paper>
      </Container>
    </Box>
  );
};

export default DepartmentForm;
