import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link as RouterLink } from 'react-router-dom';
import { 
  Box, Container, Typography, Button, Paper, 
  Grid, TextField, FormControl, InputLabel, Select, MenuItem,
  CircularProgress, Alert, FormHelperText
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import dayjs from 'dayjs';

const DepartmentForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isEditMode = Boolean(id);
  
  const [loading, setLoading] = useState(isEditMode);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  
  const [formData, setFormData] = useState({
    name: '',
    head: '',
    description: '',
    foundedYear: 2000,
    location: '',
    status: 'ACTIVE',
    budget: '',
    email: '',
    phone: ''
  });

  useEffect(() => {
    if (isEditMode) {
      const fetchDepartment = async () => {
        try {
          // Mock data for edit - replace with actual API call
          const mockDepartment = {
            id: 1,
            name: 'Computer Science',
            head: 'Dr. Alan Turing',
            description: 'Department focused on computer science and software engineering.',
            foundedYear: 1985,
            location: 'Building A, Floor 3',
            status: 'ACTIVE',
            budget: '500000',
            email: 'cs@university.edu',
            phone: '(123) 456-7890'
          };
          
          setFormData(mockDepartment);
          setLoading(false);
        } catch (err) {
          console.error('Error loading department:', err);
          setError('Failed to load department data. Please try again.');
          setLoading(false);
        }
      };

      fetchDepartment();
    }
  }, [id, isEditMode]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Simulating API call - replace with actual call to backend
      await new Promise(resolve => setTimeout(resolve, 500));
      
      console.log('Submitting department data:', formData);
      
      setSuccess(true);
      setLoading(false);
      
      // Redirect after a short delay
      setTimeout(() => {
        navigate('/departments');
      }, 1500);
    } catch (err) {
      console.error('Error saving department:', err);
      setError('Failed to save department. Please check your input and try again.');
      setLoading(false);
    }
  };

  return (
    <Box className="page-container">
      <Container maxWidth="md">
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <Button
            variant="outlined"
            component={RouterLink}
            to="/departments"
            sx={{ mr: 2 }}
          >
            Back
          </Button>
          <Typography variant="h4" component="h1">
            {isEditMode ? 'Edit Department' : 'Add New Department'}
          </Typography>
        </Box>
        
        <Paper elevation={3} sx={{ p: 4 }}>
          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}
          
          {success && (
            <Alert severity="success" sx={{ mb: 3 }}>
              Department {isEditMode ? 'updated' : 'created'} successfully!
            </Alert>
          )}
          
          {loading && !error ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
              <CircularProgress />
            </Box>
          ) : (
            <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="name"
                    label="Department Name"
                    value={formData.name}
                    onChange={handleChange}
                    fullWidth
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="head"
                    label="Department Head"
                    value={formData.head}
                    onChange={handleChange}
                    fullWidth
                    required
                    helperText="Name and title of department head"
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    name="description"
                    label="Description"
                    value={formData.description}
                    onChange={handleChange}
                    fullWidth
                    multiline
                    rows={3}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="foundedYear"
                    label="Founded Year"
                    type="number"
                    value={formData.foundedYear}
                    onChange={handleChange}
                    fullWidth
                    required
                    inputProps={{ min: 1800, max: new Date().getFullYear() }}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="location"
                    label="Location"
                    value={formData.location}
                    onChange={handleChange}
                    fullWidth
                    helperText="Building and floor"
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="email"
                    label="Department Email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    fullWidth
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="phone"
                    label="Department Phone"
                    value={formData.phone}
                    onChange={handleChange}
                    fullWidth
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="budget"
                    label="Annual Budget"
                    value={formData.budget}
                    onChange={handleChange}
                    fullWidth
                    type="number"
                    InputProps={{
                      startAdornment: '$',
                    }}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <FormControl fullWidth required>
                    <InputLabel>Status</InputLabel>
                    <Select
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                      label="Status"
                    >
                      <MenuItem value="ACTIVE">Active</MenuItem>
                      <MenuItem value="INACTIVE">Inactive</MenuItem>
                    </Select>
                    <FormHelperText>Current state of the department</FormHelperText>
                  </FormControl>
                </Grid>
                <Grid item xs={12}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                    <Button
                      variant="outlined"
                      onClick={() => navigate('/departments')}
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      disabled={loading}
                    >
                      {isEditMode ? 'Update Department' : 'Add Department'}
                    </Button>
                  </Box>
                </Grid>
              </Grid>
            </Box>
          )}
        </Paper>
      </Container>
    </Box>
  );
};

export default DepartmentForm;
