import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link as RouterLink } from 'react-router-dom';
import { 
  Box, Container, Typography, Button, Paper, 
  Grid, TextField, FormControl, InputLabel, Select, MenuItem,
  CircularProgress, Alert, FormHelperText
} from '@mui/material';
import { getCourseById, createCourse, updateCourse } from '../../services/courseService';
import { getDepartments } from '../../services/departmentService';

const CourseForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isEditMode = Boolean(id);
  
  const [loading, setLoading] = useState(isEditMode);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [departments, setDepartments] = useState([]);
  
  const [formData, setFormData] = useState({
    code: '',
    name: '',
    description: '',
    department: '',
    credits: 3,
    capacity: 30,
    status: 'ACTIVE'
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch departments for the dropdown
        const departmentsData = await getDepartments();
        setDepartments(departmentsData.map(dept => dept.name));
        
        // If in edit mode, fetch the course data
        if (isEditMode) {
          const courseData = await getCourseById(id);
          setFormData({
            code: courseData.code || '',
            name: courseData.name || '',
            description: courseData.description || '',
            department: courseData.department || '',
            credits: courseData.credits || 3,
            capacity: courseData.capacity || 30,
            status: courseData.status || 'ACTIVE'
          });
        }
        
        setLoading(false);
      } catch (err) {
        console.error('Error loading form data:', err);
        setError('Failed to load form data. Please try again.');
        setLoading(false);
      }
    };

    fetchData();
  }, [id, isEditMode]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Create a copy of the form data to avoid modifying state directly
      const submissionData = { ...formData };
      
      // Convert numeric strings to numbers
      if (typeof submissionData.credits === 'string') {
        submissionData.credits = parseInt(submissionData.credits, 10);
      }
      
      if (typeof submissionData.capacity === 'string') {
        submissionData.capacity = parseInt(submissionData.capacity, 10);
      }
      
      console.log('Submitting course data:', submissionData);
      
      if (isEditMode) {
        await updateCourse(id, submissionData);
      } else {
        await createCourse(submissionData);
      }
      
      setSuccess(true);
      setLoading(false);
      
      // Redirect after a short delay
      setTimeout(() => {
        navigate('/courses');
      }, 1500);
    } catch (err) {
      console.error('Error saving course:', err);
      setError('Failed to save course. Please check your input and try again.');
      setLoading(false);
    }
  };

  return (
    <Box className="page-container">
      <Container maxWidth="md">
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <Button
            variant="outlined"
            component={RouterLink}
            to="/courses"
            sx={{ mr: 2 }}
          >
            Back
          </Button>
          <Typography variant="h4" component="h1">
            {isEditMode ? 'Edit Course' : 'Add New Course'}
          </Typography>
        </Box>
        
        <Paper elevation={3} sx={{ p: 4 }}>
          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}
          
          {success && (
            <Alert severity="success" sx={{ mb: 3 }}>
              Course {isEditMode ? 'updated' : 'created'} successfully!
            </Alert>
          )}
          
          {loading && !error ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
              <CircularProgress />
            </Box>
          ) : (
            <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="code"
                    label="Course Code"
                    value={formData.code}
                    onChange={handleChange}
                    fullWidth
                    required
                    helperText="e.g., CS101, MTH201"
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="name"
                    label="Course Name"
                    value={formData.name}
                    onChange={handleChange}
                    fullWidth
                    required
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    name="description"
                    label="Description"
                    value={formData.description}
                    onChange={handleChange}
                    fullWidth
                    multiline
                    rows={3}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <FormControl fullWidth required>
                    <InputLabel>Department</InputLabel>
                    <Select
                      name="department"
                      value={formData.department}
                      onChange={handleChange}
                      label="Department"
                    >
                      {departments.map((dept) => (
                        <MenuItem key={dept} value={dept}>
                          {dept}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="credits"
                    label="Credits"
                    type="number"
                    value={formData.credits}
                    onChange={handleChange}
                    fullWidth
                    required
                    inputProps={{ min: 1, max: 6 }}
                    helperText="1-6 credits"
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    name="capacity"
                    label="Capacity"
                    type="number"
                    value={formData.capacity}
                    onChange={handleChange}
                    fullWidth
                    required
                    inputProps={{ min: 5, max: 300 }}
                    helperText="Maximum number of students"
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <FormControl fullWidth required>
                    <InputLabel>Status</InputLabel>
                    <Select
                      name="status"
                      value={formData.status}
                      onChange={handleChange}
                      label="Status"
                    >
                      <MenuItem value="ACTIVE">Active</MenuItem>
                      <MenuItem value="INACTIVE">Inactive</MenuItem>
                    </Select>
                    <FormHelperText>Current state of the course</FormHelperText>
                  </FormControl>
                </Grid>
                <Grid item xs={12}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                    <Button
                      variant="outlined"
                      onClick={() => navigate('/courses')}
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      disabled={loading}
                    >
                      {isEditMode ? 'Update Course' : 'Add Course'}
                    </Button>
                  </Box>
                </Grid>
              </Grid>
            </Box>
          )}
        </Paper>
      </Container>
    </Box>
  );
};

export default CourseForm;
