import api from './api';

const ENDPOINT = '/departments';
const STORAGE_KEY = 'mock_departments';

// Initialize mock data if not already in localStorage
const initializeMockData = () => {
  if (!localStorage.getItem(STORAGE_KEY)) {
    const initialDepartments = [
      { id: 1, name: 'Computer Science', head: 'Dr. Alan Turing', foundedYear: 1985, status: 'ACTIVE', studentsCount: 120, coursesCount: 15 },
      { id: 2, name: 'Mathematics', head: 'Dr. Ada Lovelace', foundedYear: 1950, status: 'ACTIVE', studentsCount: 85, coursesCount: 12 },
      { id: 3, name: 'Physics', head: 'Dr. Richard Feynman', foundedYear: 1960, status: 'ACTIVE', studentsCount: 65, coursesCount: 10 },
      { id: 4, name: 'Biology', head: 'Dr. Jane Goodall', foundedYear: 1970, status: 'ACTIVE', studentsCount: 90, coursesCount: 14 },
      { id: 5, name: 'History', head: 'Dr. Howard Zinn', foundedYear: 1955, status: 'INACTIVE', studentsCount: 40, coursesCount: 8 },
      { id: 6, name: 'English', head: 'Dr. Maya Angelou', foundedYear: 1965, status: 'ACTIVE', studentsCount: 75, coursesCount: 9 },
    ];
    localStorage.setItem(STORAGE_KEY, JSON.stringify(initialDepartments));
  }
};

// Helper to get departments from localStorage
const getMockDepartments = () => {
  initializeMockData();
  return JSON.parse(localStorage.getItem(STORAGE_KEY) || '[]');
};

// Helper to save departments to localStorage
const saveMockDepartments = (departments) => {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(departments));
};

// Fetch all departments
export const getDepartments = async () => {
  try {
    // For now, return mock data from localStorage
    return getMockDepartments();
    
    // Uncomment when backend is ready
    // const response = await api.get(ENDPOINT);
    // return response.data;
  } catch (error) {
    console.error('Error fetching departments:', error);
    return []; // Return empty array instead of throwing to prevent UI crashes
  }
};

// Fetch a department by ID
export const getDepartmentById = async (id) => {
  try {
    // Get department from localStorage
    const departments = getMockDepartments();
    const department = departments.find(d => d.id === parseInt(id));
    
    if (department) {
      // Add additional details not stored in the list view
      return {
        ...department,
        description: department.description || 'Department focused on computer science, software engineering, and related disciplines.',
        location: department.location || 'Building A, Floor 3',
        budget: department.budget || 500000,
        email: department.email || 'cs@university.edu',
        phone: department.phone || '(123) 456-7890',
        courses: department.courses || [
          { id: 1, code: 'CS101', name: 'Introduction to Computer Science', credits: 3 },
          { id: 2, code: 'CS201', name: 'Data Structures', credits: 4 },
          { id: 3, code: 'CS301', name: 'Algorithms', credits: 4 },
        ],
        faculty: department.faculty || [
          { id: 1, name: 'Dr. Alan Turing', position: 'Department Head', email: 'turing@university.edu' },
          { id: 2, name: 'Dr. Grace Hopper', position: 'Professor', email: 'hopper@university.edu' },
          { id: 3, name: 'Dr. John von Neumann', position: 'Associate Professor', email: 'neumann@university.edu' },
        ]
      };
    }
    
    throw new Error(`Department with ID ${id} not found`);
    
    // Uncomment when backend is ready
    // const response = await api.get(`${ENDPOINT}/${id}`);
    // return response.data;
  } catch (error) {
    console.error(`Error fetching department ${id}:`, error);
    throw error;
  }
};

// Create a new department
export const createDepartment = async (departmentData) => {
  try {
    // Get current departments
    const departments = getMockDepartments();
    
    // Create new department with ID
    const newDepartment = {
      ...departmentData,
      id: Math.max(0, ...departments.map(d => d.id)) + 1,
      studentsCount: 0,
      coursesCount: 0
    };
    
    // Add to departments and save
    departments.push(newDepartment);
    saveMockDepartments(departments);
    
    console.log('Created department:', newDepartment);
    return newDepartment;
    
    // Uncomment when backend is ready
    // const response = await api.post(ENDPOINT, departmentData);
    // return response.data;
  } catch (error) {
    console.error('Error creating department:', error);
    throw error;
  }
};

// Update a department
export const updateDepartment = async (id, departmentData) => {
  try {
    // Get current departments
    const departments = getMockDepartments();
    const departmentIndex = departments.findIndex(d => d.id === parseInt(id));
    
    if (departmentIndex === -1) {
      throw new Error(`Department with ID ${id} not found`);
    }
    
    // Preserve student and course counts
    const existingCounts = {
      studentsCount: departments[departmentIndex].studentsCount || 0,
      coursesCount: departments[departmentIndex].coursesCount || 0
    };
    
    // Update department
    const updatedDepartment = { 
      ...departmentData, 
      id: parseInt(id),
      ...existingCounts
    };
    
    departments[departmentIndex] = updatedDepartment;
    
    // Save updated departments
    saveMockDepartments(departments);
    
    console.log(`Updated department ${id}:`, updatedDepartment);
    return updatedDepartment;
    
    // Uncomment when backend is ready
    // const response = await api.put(`${ENDPOINT}/${id}`, departmentData);
    // return response.data;
  } catch (error) {
    console.error(`Error updating department ${id}:`, error);
    throw error;
  }
};

// Delete a department
export const deleteDepartment = async (id) => {
  try {
    // Get current departments
    const departments = getMockDepartments();
    const filteredDepartments = departments.filter(d => d.id !== parseInt(id));
    
    // Save filtered departments
    saveMockDepartments(filteredDepartments);
    
    console.log(`Deleted department ${id}`);
    return true;
    
    // Uncomment when backend is ready
    // await api.delete(`${ENDPOINT}/${id}`);
    // return true;
  } catch (error) {
    console.error(`Error deleting department ${id}:`, error);
    throw error;
  }
};

// Get courses in a department
export const getCoursesByDepartmentId = async (departmentId) => {
  try {
    // Get department details which already has courses
    const department = await getDepartmentById(departmentId);
    return department.courses || [];
    
    // Uncomment when backend is ready
    // const response = await api.get(`${ENDPOINT}/${departmentId}/courses`);
    // return response.data;
  } catch (error) {
    console.error(`Error fetching courses for department ${departmentId}:`, error);
    return [];
  }
};
